import React, { useState } from 'react';
import postLink from '../API/postLink';

function LinkForm() {
    const [ inputUrl, setInputUrl ] = useState("");

    const handleInputUrl = (e) =>
    {
        setInputUrl(e.target.value);
    }

    const addUrl = () =>
    {
      const newUrl =
      {
        link: inputUrl,
        screenshot: "screenshotN",
        title: "titleN",
        description: "descriptionN"
      }
      console.log(newUrl);

      postLink(newUrl);

      setInputUrl("");
    }

    return (
        <div className="container col-xxl-8 px-4 py-3">
                <div>
                    <form className="form-floating row flex-lg-row g-5 py-5" action="">
                        <div className="col-lg-12">
                            <div className="form-floating mb-3">
                                <input name="link" type="text" className="form-control linkURL" id="inputLink" value={ inputUrl } required onChange={ handleInputUrl }/>
                                <label htmlFor="inputLink">Shorten your link</label>
                            </div>
                        </div>

                        <div className="form-floating col-lg-6 mb-2">
                            <input name="title" type="text" className="form-control linkURL" id="inputTitle" value="" required/>
                            <label htmlFor="inputTitle">Titel</label>
                        </div>
                        <div className="form-floating col-lg-6 mb-2">
                            <textarea name="description" className="form-control linkURL" value="" id="inputDescription" required></textarea>
                            <label htmlFor="inputDescription">Beschreibung</label>
                        </div>

                        <div className="text-center mt-4"><button className="btn btn-lg px-4 fill" onClick={ addUrl }>Shorten</button></div>

                    </form>
                </div>
        </div>
    )
}

export default LinkForm;
